import React, { useState, useEffect } from 'react';
import ApiService from '../services/api'; // Import your ApiService

function DatabaseStats() {
  const [stats, setStats] = useState({
    users: 0,
    courses: 0,
    enrollments: 0,
    loading: true
  });
  const [recentEnrollments, setRecentEnrollments] = useState([]);
  const [topCourses, setTopCourses] = useState([]);
  const [topStudents, setTopStudents] = useState([]);
  const [userDistribution, setUserDistribution] = useState({});
  const [enrollmentStatus, setEnrollmentStatus] = useState([]);
  const [categories, setCategories] = useState([]);
  const [metrics, setMetrics] = useState({});
  const [error, setError] = useState('');
  const [backendStatus, setBackendStatus] = useState('checking');

  useEffect(() => {
    loadDatabaseStats();
  }, []);

  const loadDatabaseStats = async () => {
    console.log('ðŸ”„ Loading database stats...');
    setStats(prev => ({ ...prev, loading: true }));
    setError('');
    setBackendStatus('loading');
    
    try {
      // Use ApiService instead of direct fetch
      const data = await ApiService.getAnalyticsSummary();
      console.log('ðŸ“Š Analytics Summary Data:', data);
      
      if (data.success) {
        const analytics = data.data;
        console.log('âœ… Analytics data received:', analytics);
        
        setBackendStatus('connected');
        setStats({
          users: analytics.totals?.users || 0,
          courses: analytics.totals?.courses || 0,
          enrollments: analytics.totals?.enrollments || 0,
          loading: false
        });
        
        // Change TO:
        setRecentEnrollments(analytics.recentEnrollments || []);
        setTopCourses(analytics.topCourses || []);
        setTopStudents([]);  // âœ… Empty array since API doesn't provide
        setUserDistribution(analytics.distribution?.users || {});
        setEnrollmentStatus([]);  // âœ… Empty array since API doesn't provide
        setCategories(analytics.distribution?.categories || []);
        setMetrics(analytics.metrics || {});
        
        // Log for debugging
        console.log('ðŸ“ˆ Parsed stats:', {
          users: analytics.totals?.users,
          courses: analytics.totals?.courses,
          enrollments: analytics.totals?.enrollments,
          hasRecentEnrollments: analytics.recentEnrollments?.length || 0,
          hasTopCourses: analytics.topCourses?.length || 0
        });
      } else {
        throw new Error(data.error || 'Failed to load analytics');
      }
    } catch (error) {
      console.error('âŒ Failed to load database stats:', error);
      setError(`Failed to load: ${error.message}`);
      setBackendStatus('error');
      setStats(prev => ({ ...prev, loading: false }));
      
      // Try alternative endpoint as fallback
      tryFallback();
    }
  };

  const tryFallback = async () => {
    console.log('ðŸ”„ Trying fallback endpoints...');
    try {
      // Try getting basic stats
      const statsRes = await ApiService.getStats();
      if (statsRes.success) {
        console.log('âœ… Fallback stats received:', statsRes);
        setStats({
          users: statsRes.data?.users || 0,
          courses: statsRes.data?.courses || 0,
          enrollments: statsRes.data?.enrollments || 0,
          loading: false
        });
        setBackendStatus('partial');
      }
    } catch (fallbackError) {
      console.error('âŒ Fallback also failed:', fallbackError);
    }
  };

  const getEnrollmentStatusColor = (status) => {
    switch(status) {
      case 'active': return 'primary';
      case 'completed': return 'success';
      case 'dropped': return 'danger';
      default: return 'secondary';
    }
  };

  // Calculate percentages safely
  const calculatePercentage = (part, total) => {
    if (!total || total === 0) return 0;
    return Math.round((part / total) * 100);
  };

  if (stats.loading) {
    return (
      <div className="card">
        <div className="card-body text-center">
          <div className="spinner-border text-primary" role="status">
            <span className="visually-hidden">Loading database stats...</span>
          </div>
          <p className="mt-2">
            Loading database statistics from backend...
            <br />
            <small className="text-muted">
              Status: {backendStatus === 'checking' ? 'Checking connection...' : 'Loading data...'}
            </small>
          </p>
        </div>
      </div>
    );
  }

  return (
    <div className="card shadow-lg">
      <div className="card-header bg-primary text-white d-flex justify-content-between align-items-center">
        <div>
          <h5 className="card-title mb-0">ðŸ“Š Database Analytics Dashboard</h5>
          <small className="opacity-75">
            {backendStatus === 'connected' ? 'âœ… Connected to MongoDB' : 
             backendStatus === 'partial' ? 'âš ï¸ Partial connection' : 
             'âŒ Connection failed'}
          </small>
          // Also update the enrollment status calculation (lines ~140-150):
          <div className="small text-muted">
            Active: {metrics.activeRatio ? Math.round(metrics.activeRatio) : 0}% | 
            Completed: {metrics.completionRate || 0}%
          </div>
        </div>
        <div>
          <button 
            className="btn btn-sm btn-light me-2"
            onClick={loadDatabaseStats}
            title="Refresh data"
          >
            ðŸ”„ Refresh
          </button>
          <button 
            className="btn btn-sm btn-outline-light"
            onClick={() => console.log({
              stats,
              userDistribution,
              enrollmentStatus,
              categories,
              metrics
            })}
            title="Debug data"
          >
            ðŸ› Debug
          </button>
        </div>
      </div>
      <div className="card-body">
        
        {error && (
          <div className="alert alert-warning mb-3">
            <strong>âš ï¸ Warning:</strong> {error}
            <div className="mt-2">
              <small>
                Backend: <a href="http://localhost:5000" target="_blank" rel="noreferrer">http://localhost:5000</a> | 
                Check if MongoDB Analytics Backend is running.
              </small>
            </div>
          </div>
        )}

        {/* Summary Cards */}
        <div className="row mb-4">
          <div className="col-md-3 mb-3">
            <div className="card border-primary h-100">
              <div className="card-body text-center">
                <div className="display-6 text-primary">{stats.users}</div>
                <div className="card-title">Total Users</div>
                <div className="small text-muted">
                  Students: {userDistribution.students || 0} | 
                  Instructors: {userDistribution.instructors || 0} | 
                  Admins: {userDistribution.admins || 0}
                </div>
                <div className="mt-2 small">
                  <span className={`badge ${backendStatus === 'connected' ? 'bg-success' : 'bg-warning'}`}>
                    {backendStatus === 'connected' ? 'Live Data' : 'Sample'}
                  </span>
                </div>
              </div>
            </div>
          </div>
          
          <div className="col-md-3 mb-3">
            <div className="card border-success h-100">
              <div className="card-body text-center">
                <div className="display-6 text-success">{stats.courses}</div>
                <div className="card-title">Courses</div>
                <div className="small text-muted">
                  {categories.length} categories | 
                  {metrics.averageEnrollments ? ` ${metrics.averageEnrollments} avg enrollment` : ' Loading...'}
                </div>
                <div className="mt-2 small">
                  {stats.courses === 0 && (
                    <span className="badge bg-danger">No courses found</span>
                  )}
                </div>
              </div>
            </div>
          </div>
          
          <div className="col-md-3 mb-3">
            <div className="card border-warning h-100">
              <div className="card-body text-center">
                <div className="display-6 text-warning">{stats.enrollments}</div>
                <div className="card-title">Enrollments</div>
                <div className="small text-muted">
                  Active: {enrollmentStatus.find(e => e._id === 'active')?.count || 0} | 
                  Completed: {enrollmentStatus.find(e => e._id === 'completed')?.count || 0}
                </div>
              </div>
            </div>
          </div>
          
          <div className="col-md-3 mb-3">
            <div className="card border-info h-100">
              <div className="card-body text-center">
                <div className="display-6 text-info">{metrics.completionRate || 0}%</div>
                <div className="card-title">Completion Rate</div>
                <div className="small text-muted">
                  {topStudents.filter(s => s.completedCourses > 0).length} students completed courses
                </div>
              </div>
            </div>
          </div>
        </div>

        {/* User Distribution & Enrollment Status */}
        <div className="row mb-4">
          <div className="col-md-6">
            <div className="card h-100">
              <div className="card-header">
                <h6 className="mb-0">ðŸ‘¥ User Distribution</h6>
              </div>
              <div className="card-body">
                <div className="row text-center">
                  <div className="col-4">
                    <div className="p-3">
                      <div className="text-primary fs-3">{userDistribution.students || 0}</div>
                      <small className="text-muted">Students</small>
                      <div className="small">
                        {calculatePercentage(userDistribution.students || 0, stats.users)}%
                      </div>
                    </div>
                  </div>
                  <div className="col-4">
                    <div className="p-3">
                      <div className="text-warning fs-3">{userDistribution.instructors || 0}</div>
                      <small className="text-muted">Instructors</small>
                      <div className="small">
                        {calculatePercentage(userDistribution.instructors || 0, stats.users)}%
                      </div>
                    </div>
                  </div>
                  <div className="col-4">
                    <div className="p-3">
                      <div className="text-danger fs-3">{userDistribution.admins || 0}</div>
                      <small className="text-muted">Admins</small>
                      <div className="small">
                        {calculatePercentage(userDistribution.admins || 0, stats.users)}%
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
          
          <div className="col-md-6">
            <div className="card h-100">
              <div className="card-header">
                <h6 className="mb-0">ðŸŽ¯ Enrollment Status</h6>
              </div>
              
              <div className="card-body">
                <div className="row text-center">
                  {enrollmentStatus.length > 0 ? (
                    enrollmentStatus.map((item, index) => (
                      <div className="col-4" key={index}>
                        <div className="p-3">
                          <div className={`fs-3 text-${getEnrollmentStatusColor(item._id)}`}>
                            {item.count}
                          </div>
                          <small className="text-muted">{item._id}</small>
                          <div className="small">
                            {calculatePercentage(item.count, stats.enrollments)}%
                          </div>
                        </div>
                      </div>
                    ))
                  ) : (
                    <div className="col-12 text-center text-muted">
                      No enrollment status data available
                    </div>
                  )}
                </div>
              </div>
            </div>
          </div>
        </div>

        {/* Top Courses & Top Students */}
        {(topCourses.length > 0 || topStudents.length > 0) && (
          <div className="row mb-4">
            {topCourses.length > 0 && (
              <div className="col-md-6">
                <div className="card h-100">
                  <div className="card-header">
                    <h6 className="mb-0">ðŸ† Top Courses</h6>
                  </div>
                  <div className="card-body">
                    <div className="list-group">
                      {topCourses.slice(0, 5).map((course, index) => (
                        <div key={index} className="list-group-item">
                          <div className="d-flex justify-content-between align-items-center">
                            <div>
                              <strong>{course.title}</strong>
                              <div className="small text-muted">
                                {course.category} â€¢ {course.duration} â€¢ â­ {course.rating}
                              </div>
                            </div>
                            <span className="badge bg-primary">
                              {course.enrolledStudents} students
                            </span>
                          </div>
                        </div>
                      ))}
                    </div>
                  </div>
                </div>
              </div>
            )}
            
            {topStudents.length > 0 && (
              <div className="col-md-6">
                <div className="card h-100">
                  <div className="card-header">
                    <h6 className="mb-0">ðŸŒŸ Top Students</h6>
                  </div>
                  <div className="card-body">
                    <div className="list-group">
                      {topStudents.slice(0, 5).map((student, index) => (
                        <div key={index} className="list-group-item">
                          <div className="d-flex justify-content-between align-items-center">
                            <div>
                              <strong>{student.name}</strong>
                              <div className="small text-muted">{student.email}</div>
                            </div>
                            <div className="text-end">
                              <div className="badge bg-info me-2">
                                {student.enrolledCourses} enrolled
                              </div>
                              <div className="badge bg-success">
                                {student.completedCourses} completed
                              </div>
                            </div>
                          </div>
                        </div>
                      ))}
                    </div>
                  </div>
                </div>
              </div>
            )}
          </div>
        )}

        {/* Recent Enrollments */}
        {recentEnrollments.length > 0 ? (
          <div className="card mb-4">
            <div className="card-header">
              <h6 className="mb-0">ðŸ“ Recent Enrollments</h6>
            </div>
            <div className="card-body">
              <div className="table-responsive">
                <table className="table table-sm table-hover">
                  <thead>
                    <tr>
                      <th>Student</th>
                      <th>Course</th>
                      <th>Status</th>
                      <th>Progress</th>
                      <th>Enrolled At</th>
                    </tr>
                  </thead>
                  <tbody>
                    {recentEnrollments.map((enrollment, index) => (
                      <tr key={index}>
                        <td>{enrollment.userName}</td>
                        <td>{enrollment.courseTitle}</td>
                        <td>
                          <span className={`badge bg-${getEnrollmentStatusColor(enrollment.status)}`}>
                            {enrollment.status}
                          </span>
                        </td>
                        <td>
                          <div className="d-flex align-items-center">
                            <div className="progress flex-grow-1 me-2" style={{ height: '6px' }}>
                              <div 
                                className="progress-bar" 
                                style={{ width: `${enrollment.progress}%` }}
                              ></div>
                            </div>
                            <small>{enrollment.progress}%</small>
                          </div>
                        </td>
                        <td>
                          {new Date(enrollment.enrolledAt).toLocaleDateString()}
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>
          </div>
        ) : (
          <div className="alert alert-info mb-4">
            <i className="fas fa-info-circle me-2"></i>
            No recent enrollments found. Check backend connection.
          </div>
        )}

        {/* Course Categories */}
        {categories.length > 0 && (
          <div className="card mb-4">
            <div className="card-header">
              <h6 className="mb-0">ðŸ“š Course Categories</h6>
            </div>
            <div className="card-body">
              <div className="row">
                {categories.map((category, index) => (
                  <div className="col-md-4 mb-3" key={index}>
                    <div className="card border-secondary h-100">
                      <div className="card-body">
                        <h6 className="card-title">{category._id}</h6>
                        <div className="d-flex justify-content-between">
                          <div>
                            <span className="badge bg-secondary">
                              {category.count} courses
                            </span>
                          </div>
                          <div>
                            <span className="badge bg-info">
                              {category.totalEnrolled} students
                            </span>
                          </div>
                        </div>
                        <div className="mt-2 small text-muted">
                          Avg: {Math.round(category.totalEnrolled / category.count)} students per course
                        </div>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          </div>
        )}

        {/* Quick Links */}
        <div className="mt-4 pt-3 border-top">
          <div className="d-flex justify-content-between align-items-center">
            <div className="d-flex flex-wrap gap-2">
              <a 
                href="http://localhost:5000/api/users" 
                target="_blank" 
                rel="noopener noreferrer"
                className="btn btn-outline-primary btn-sm"
              >
                ðŸ‘¤ Users API
              </a>
              <a 
                href="http://localhost:5000/api/courses" 
                target="_blank" 
                rel="noopener noreferrer"
                className="btn btn-outline-success btn-sm"
              >
                ðŸ“š Courses API
              </a>
              <a 
                href="http://localhost:5000/api/enrollments" 
                target="_blank" 
                rel="noopener noreferrer"
                className="btn btn-outline-warning btn-sm"
              >
                ðŸŽ¯ Enrollments API
              </a>
              <a 
                href="http://localhost:5000/api/analytics/summary" 
                target="_blank" 
                rel="noopener noreferrer"
                className="btn btn-outline-info btn-sm"
              >
                ðŸ“Š Analytics API
              </a>
            </div>
            <div className="text-end">
              <small className="text-muted">
                Backend: <a href="http://localhost:5000" target="_blank" rel="noreferrer">Port 5000</a>
                <br />
                Status: {backendStatus}
              </small>
            </div>
          </div>
        </div>
      </div>
      <div className="card-footer text-muted d-flex justify-content-between">
        <small>Last updated: {new Date().toLocaleString()}</small>
        <small>
          Data from: {backendStatus === 'connected' ? 'MongoDB Backend' : 'Fallback/Error'}
        </small>
      </div>
    </div>
  );
}

export default DatabaseStats;
