import React, { useState, useEffect } from 'react';
import ApiService from '../services/api';

function Dashboard() {
  const [userCourses, setUserCourses] = useState([]);
  const [availableCourses, setAvailableCourses] = useState([]);
  const [loading, setLoading] = useState(true);
  const [currentUser] = useState({
    id: 'STU001', // Make sure this matches a user in your database
    name: 'John Smith',
    email: 'john@example.com'
  });

  // Load dashboard data
  useEffect(() => {
    const loadDashboardData = async () => {
      try {
        setLoading(true);
        console.log('Loading dashboard data for user:', currentUser.id);
        
        // Fetch user's enrollments
        const enrollmentsResponse = await ApiService.getUserEnrollments(currentUser.id);
        console.log('Enrollments response:', enrollmentsResponse);
        
        if (enrollmentsResponse.success) {
          const userEnrollments = enrollmentsResponse.data || [];
          console.log('User enrollments:', userEnrollments);
          setUserCourses(userEnrollments);
        }
        
        // Fetch all available courses
        const coursesResponse = await ApiService.getCourses();
        console.log('Courses response:', coursesResponse);
        
        if (coursesResponse.success) {
          const allCourses = coursesResponse.data || [];
          console.log('All courses:', allCourses);
          setAvailableCourses(allCourses.slice(0, 6)); // Show only 6 courses
        }
        
      } catch (error) {
        console.error('Error loading dashboard data:', error);
        
        // Fallback data for testing
        setUserCourses([
          { 
            courseId: 'WEB101', 
            courseTitle: 'Web Development Fundamentals', 
            progress: 75, 
            status: 'active' 
          },
          { 
            courseId: 'DS201', 
            courseTitle: 'Data Science with Python', 
            progress: 45, 
            status: 'active' 
          }
        ]);
        
        setAvailableCourses([
          { 
            courseId: 'AI301', 
            title: 'Machine Learning Fundamentals', 
            description: 'Introduction to AI and ML', 
            category: 'AI/ML', 
            level: 'Advanced' 
          },
          { 
            courseId: 'REA201', 
            title: 'React for Beginners', 
            description: 'Learn React framework', 
            category: 'Web Development', 
            level: 'Intermediate' 
          },
          { 
            courseId: 'PYT101', 
            title: 'Python Programming', 
            description: 'Python from basics to advanced', 
            category: 'Programming', 
            level: 'Beginner' 
          }
        ]);
      } finally {
        setLoading(false);
      }
    };

    loadDashboardData();
  }, [currentUser.id]);

  // Handle course enrollment
  const handleEnroll = async (courseId, courseTitle) => {
    try {
      const result = await ApiService.enrollCourse(currentUser.id, courseId);
      
      if (result.success) {
        alert(`Successfully enrolled in ${courseTitle}!`);
        
        // Update local state
        setUserCourses(prev => [...prev, {
          courseId,
          courseTitle,
          progress: 0,
          status: 'active'
        }]);
      } else {
        alert(`Failed to enroll: ${result.error || 'Unknown error'}`);
      }
    } catch (error) {
      console.error('Enrollment error:', error);
      alert('Failed to enroll. Please try again.');
    }
  };

  // Handle progress update
  const handleProgress = async (courseId, courseTitle) => {
    const newProgress = Math.min(100, (userCourses.find(c => c.courseId === courseId)?.progress || 0) + 10);
    
    // Update local state
    setUserCourses(prev => 
      prev.map(course => 
        course.courseId === courseId 
          ? { ...course, progress: newProgress }
          : course
      )
    );
    
    alert(`Progress updated for ${courseTitle}: ${newProgress}%`);
  };

  if (loading) {
    return (
      <div className="container mt-4 text-center">
        <div className="spinner-border text-primary" role="status">
          <span className="visually-hidden">Loading...</span>
        </div>
        <p className="mt-2">Loading your dashboard...</p>
      </div>
    );
  }

  return (
    <div className="container mt-4">
      <h1 className="mb-4">
        <i className="fas fa-graduation-cap me-2"></i>
        Learning Dashboard
      </h1>
      
      {/* Welcome Message */}
      <div className="alert alert-info mb-4">
        <div className="d-flex align-items-center">
          <i className="fas fa-user-circle fa-2x me-3"></i>
          <div>
            <h5 className="mb-1">Welcome back, {currentUser.name}!</h5>
            <p className="mb-0">Continue your learning journey</p>
          </div>
        </div>
      </div>
      
      {/* Stats Overview */}
      <div className="row mb-4">
        <div className="col-md-4">
          <div className="card text-center">
            <div className="card-body">
              <h1 className="display-4 text-primary">{userCourses.length}</h1>
              <p className="card-text">Active Courses</p>
            </div>
          </div>
        </div>
        <div className="col-md-4">
          <div className="card text-center">
            <div className="card-body">
              <h1 className="display-4 text-success">
                {userCourses.length > 0 
                  ? Math.round(userCourses.reduce((sum, c) => sum + c.progress, 0) / userCourses.length)
                  : 0}%
              </h1>
              <p className="card-text">Average Progress</p>
            </div>
          </div>
        </div>
        <div className="col-md-4">
          <div className="card text-center">
            <div className="card-body">
              <h1 className="display-4 text-warning">{availableCourses.length}</h1>
              <p className="card-text">Available Courses</p>
            </div>
          </div>
        </div>
      </div>

      {/* My Courses Section */}
      <div className="card mb-4 shadow-sm">
        <div className="card-header bg-primary text-white">
          <h4 className="mb-0">
            <i className="fas fa-book-open me-2"></i>
            My Courses ({userCourses.length})
          </h4>
        </div>
        <div className="card-body">
          {userCourses.length === 0 ? (
            <div className="text-center py-4">
              <i className="fas fa-book fa-3x text-muted mb-3"></i>
              <h5>You haven't enrolled in any courses yet.</h5>
              <p className="text-muted">Browse available courses below to get started!</p>
              <a href="/courses" className="btn btn-primary">
                <i className="fas fa-search me-2"></i>
                Browse Courses
              </a>
            </div>
          ) : (
            <div className="table-responsive">
              <table className="table table-hover">
                <thead>
                  <tr>
                    <th>Course</th>
                    <th>Progress</th>
                    <th>Status</th>
                    <th>Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {userCourses.map((course, index) => (
                    <tr key={index}>
                      <td>
                        <strong>{course.courseTitle || course.title}</strong>
                        <div className="small text-muted">
                          {course.courseId || course.id} â€¢ {course.category || 'General'}
                        </div>
                      </td>
                      <td>
                        <div className="progress" style={{ height: '20px' }}>
                          <div 
                            className={`progress-bar ${course.progress === 100 ? 'bg-success' : 'bg-primary'}`}
                            role="progressbar" 
                            style={{ width: `${course.progress}%` }}
                            aria-valuenow={course.progress}
                            aria-valuemin="0"
                            aria-valuemax="100"
                          >
                            {course.progress}%
                          </div>
                        </div>
                      </td>
                      <td>
                        <span className={`badge bg-${course.status === 'completed' ? 'success' : course.status === 'active' ? 'info' : 'secondary'}`}>
                          {course.status || 'active'}
                        </span>
                      </td>
                      <td>
                        <button 
                          className="btn btn-sm btn-success me-2"
                          onClick={() => handleProgress(course.courseId || course.id, course.courseTitle || course.title)}
                          disabled={course.progress === 100}
                        >
                          <i className="fas fa-plus me-1"></i>
                          Update Progress
                        </button>
                        {course.progress === 100 && (
                          <span className="badge bg-success">
                            <i className="fas fa-check me-1"></i>
                            Completed
                          </span>
                        )}
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}
        </div>
      </div>

      {/* Available Courses Section */}
      <div className="card shadow-sm">
        <div className="card-header bg-success text-white">
          <h4 className="mb-0">
            <i className="fas fa-bookmark me-2"></i>
            Available Courses ({availableCourses.length})
          </h4>
        </div>
        <div className="card-body">
          {availableCourses.length === 0 ? (
            <div className="alert alert-warning">
              <i className="fas fa-exclamation-triangle me-2"></i>
              No courses available. Please check backend connection.
            </div>
          ) : (
            <div className="row">
              {availableCourses.map((course, index) => (
                <div className="col-md-4 mb-3" key={index}>
                  <div className="card h-100">
                    <div className="card-body">
                      <h6 className="card-title">{course.title}</h6>
                      <p className="card-text small text-muted">
                        {course.description?.substring(0, 80)}...
                      </p>
                      <div className="mb-3">
                        <span className="badge bg-primary me-1">{course.category}</span>
                        <span className="badge bg-secondary me-1">{course.level}</span>
                        {course.rating && (
                          <span className="badge bg-warning">
                            <i className="fas fa-star me-1"></i>
                            {course.rating}
                          </span>
                        )}
                      </div>
                      <button 
                        className="btn btn-primary w-100"
                        onClick={() => handleEnroll(course.courseId, course.title)}
                        disabled={userCourses.some(c => c.courseId === course.courseId)}
                      >
                        <i className="fas fa-plus-circle me-2"></i>
                        {userCourses.some(c => c.courseId === course.courseId) ? 'Already Enrolled' : 'Enroll Now'}
                      </button>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>

      {/* Quick Actions */}
      <div className="mt-4">
        <div className="card">
          <div className="card-body">
            <h5 className="card-title">
              <i className="fas fa-bolt me-2"></i>
              Quick Actions
            </h5>
            <div className="d-flex gap-2">
              <button className="btn btn-outline-primary">
                <i className="fas fa-search me-2"></i>
                Find More Courses
              </button>
              <button className="btn btn-outline-success">
                <i className="fas fa-chart-line me-2"></i>
                View Learning Analytics
              </button>
              <button className="btn btn-outline-info">
                <i className="fas fa-certificate me-2"></i>
                View Certificates
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
