class ApiService {

  // ================= SAFE FETCH =================
  static async safeFetch(url, options) {
    try {
      const response = await fetch(url, options);
      const text = await response.text();
      try {
        return JSON.parse(text);
      } catch {
        console.error('Invalid JSON response:', text);
        return { success: false, error: 'Invalid JSON response' };
      }
    } catch (error) {
      console.error('Fetch error:', error);
      return { success: false, error: 'Connection failed' };
    }
  }

  // ========== AUTHENTICATION (Port 5001) ==========
  static async register(userData) {
    return await this.safeFetch(`${COURSES_API_URL}/api/register`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(userData)
    });
  }

  static async login(credentials) {
    return await this.safeFetch(`${COURSES_API_URL}/api/login`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(credentials)
    });
  }

  // ========== COURSES (Port 5001) ==========
  static async getCourses() {
    return await this.safeFetch(`${COURSES_API_URL}/courses`);
  }

  static async getCourseById(id) {
    return await this.safeFetch(`${COURSES_API_URL}/courses/${id}`);
  }

  static async enrollCourse(userId, courseId) {
    return await this.safeFetch(`${COURSES_API_URL}/enroll`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ userId, courseId })
    });
  }

  // ========== AI & CHATBOT (Port 5001) ==========
  static async getAiRecommendations(interest, level) {
    const fallback = this.getFallbackRecommendations(interest, level);
    try {
      const data = await this.safeFetch(`${COURSES_API_URL}/api/ai/recommend`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ interest, level, context: 'course_recommendation' })
      });
      if (data.success) {
        return {
          message: data.message || 'AI Recommendations',
          recommendations: data.recommendations || [],
          courses: data.courses || []
        };
      } else {
        return fallback;
      }
    } catch (error) {
      console.error('AI Recommendation error:', error);
      return fallback;
    }
  }

  static getFallbackRecommendations(interest, level) {
    const allCourses = [
      { id: '1', title: 'Python Programming', description: 'Learn Python programming from scratch', category: 'Programming', level: 'Beginner', instructor: 'John Smith', duration: '7 weeks', rating: 4.5 },
      { id: '2', title: 'Web Development Fundamentals', description: 'Learn HTML, CSS, and JavaScript to build modern websites', category: 'Web Development', level: 'Beginner', instructor: 'Sarah Johnson', duration: '6 weeks', rating: 4.7 },
      { id: '3', title: 'Data Science with Python', description: 'Introduction to data science, analysis, and visualization', category: 'Data Science', level: 'Intermediate', instructor: 'Michael Chen', duration: '8 weeks', rating: 4.7 },
      { id: '4', title: 'React for Beginners', description: 'Learn React.js to build modern web applications', category: 'Web Development', level: 'Beginner', instructor: 'Emma Wilson', duration: '6 weeks', rating: 4.7 },
      { id: '5', title: 'Machine Learning Fundamentals', description: 'Introduction to AI and machine learning concepts', category: 'AI/ML', level: 'Intermediate', instructor: 'Dr. AI Expert', duration: '10 weeks', rating: 4.6 }
    ];

    const filteredCourses = allCourses.filter(course => {
      const interestLower = interest.toLowerCase();
      const matchesInterest =
        course.title.toLowerCase().includes(interestLower) ||
        course.description.toLowerCase().includes(interestLower) ||
        course.category.toLowerCase().includes(interestLower);
      const matchesLevel = course.level.toLowerCase() === level.toLowerCase();
      return matchesInterest && matchesLevel;
    });

    return {
      message: `Found ${filteredCourses.length} courses for "${interest}" at ${level} level`,
      recommendations: filteredCourses,
