  }

  static async getCourseEnrollmentStats(courseId) {
    return await this.safeFetch(`${SCHOOLS_API_URL}/api/courses/${courseId}/enrollments`);
  }

  // ========== SCHOOLS ==========
  static async getSchools() {
    return await this.safeFetch(`${SCHOOLS_API_URL}/api/schools`);
  }

  static async addSchool(schoolData) {
    return await this.safeFetch(`${SCHOOLS_API_URL}/api/schools`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(schoolData)
    });
  }

  static async updateSchool(id, schoolData) {
    return await this.safeFetch(`${SCHOOLS_API_URL}/api/schools/${id}`, {
      method: 'PUT',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(schoolData)
