import React, { useState, useEffect } from 'react';
import ApiService from '../services/api';

function Dashboard() {
  const [userCourses, setUserCourses] = useState([]);
  const [availableCourses, setAvailableCourses] = useState([]);
  const [loading, setLoading] = useState(true);
  const [stats, setStats] = useState({ 
    users: 0, 
    courses: 0, 
    enrollments: 0 
  });
  
  // Current user - use a real user ID from your database
const [currentUser] = useState({
  id: 'STU001', // This matches the userId in your enrollments data
  name: 'John Smith', // This matches the userName in your enrollments data
  email: 'john@example.com'
});

  // Load dashboard data
  useEffect(() => {
    const loadDashboardData = async () => {
      try {
        setLoading(true);
        console.log('Loading dashboard data for user:', currentUser.id);
        
        // ========== 1. Get Dashboard Statistics ==========
        const statsResponse = await ApiService.getStats();
        console.log('Stats response:', statsResponse);
        
        if (statsResponse.success && statsResponse.data) {
          setStats(statsResponse.data);
        } else {
          // Fallback to analytics dashboard
          const analyticsResponse = await ApiService.getAnalyticsDashboard();
          console.log('Analytics response:', analyticsResponse);
          
          if (analyticsResponse.success && analyticsResponse.data && analyticsResponse.data.totals) {
            setStats(analyticsResponse.data.totals);
          }
        }
        
        // ========== 2. Get User Enrollments ==========
        const enrollmentsResponse = await ApiService.getUserEnrollments(currentUser.id);
        console.log('Enrollments response:', enrollmentsResponse);
        
        if (enrollmentsResponse.success) {
          // Handle different response structures
          let userEnrollments = [];
          
          if (Array.isArray(enrollmentsResponse.data)) {
            // Structure 1: Direct array of enrollments
            userEnrollments = enrollmentsResponse.data;
          } else if (enrollmentsResponse.data && Array.isArray(enrollmentsResponse.data.enrollments)) {
            // Structure 2: Object with enrollments property
            userEnrollments = enrollmentsResponse.data.enrollments;
          } else if (enrollmentsResponse.enrollments) {
            // Structure 3: Root level enrollments
            userEnrollments = enrollmentsResponse.enrollments;
          }
          
          console.log('Processed enrollments:', userEnrollments);
          
          // Format enrollments for display
          const formattedEnrollments = userEnrollments.map(enrollment => {
            // Handle different enrollment structures
            return {
              courseId: enrollment.courseId || enrollment.course_id || enrollment.id || 'N/A',
              courseTitle: enrollment.courseTitle || enrollment.course_title || enrollment.title || enrollment.name || 'Unknown Course',
              progress: enrollment.progress || enrollment.completion_rate || 0,
              status: enrollment.status || enrollment.enrollment_status || 'active',
              enrolledDate: enrollment.enrolledDate || enrollment.enrollment_date || enrollment.createdAt || 'Unknown',
              // Include all original data for debugging
              original: enrollment
            };
          });
          
          setUserCourses(formattedEnrollments);
        } else {
          console.warn('Failed to fetch enrollments:', enrollmentsResponse);
          
          // Get all enrollments and filter by user ID as fallback
          const allEnrollmentsResponse = await ApiService.getEnrollments();
          if (allEnrollmentsResponse.success && Array.isArray(allEnrollmentsResponse.data)) {
            const filteredEnrollments = allEnrollmentsResponse.data
              .filter(e => e.userId === currentUser.id || e.user_id === currentUser.id)
              .map(e => ({
                courseId: e.courseId || e.course_id || 'N/A',
                courseTitle: e.courseTitle || e.course_title || 'Unknown Course',
                progress: e.progress || 0,
                status: e.status || 'active',
                enrolledDate: e.enrolledDate || e.enrollment_date || 'Unknown'
              }));
            
            setUserCourses(filteredEnrollments);
          }
        }
        
        // ========== 3. Get Available Courses ==========
        // Use the analytics backend (port 5000) for course data
        const coursesResponse = await ApiService.getCoursesFromAnalytics();
        console.log('Courses from analytics response:', coursesResponse);
        
        if (coursesResponse.success && Array.isArray(coursesResponse.data)) {
          // Format courses for display
          const formattedCourses = coursesResponse.data.map(course => ({
            courseId: course.id || course._id || course.course_id,
            title: course.title || course.name || 'Untitled Course',
            description: course.description || course.desc || 'No description available',
            category: course.category || course.department || 'General',
            level: course.level || course.difficulty || 'Beginner',
            rating: course.rating || course.average_rating,
            enrolledCount: course.enrollmentCount || course.enrolled_students || 0
          }));
          
          setAvailableCourses(formattedCourses.slice(0, 6));
        } else {
          // Fallback to AI backend courses (port 5001)
          const aiCoursesResponse = await ApiService.getCourses();
          console.log('AI Courses response:', aiCoursesResponse);
          
          if (Array.isArray(aiCoursesResponse)) {
            setAvailableCourses(aiCoursesResponse.slice(0, 6));
          }
        }
        
      } catch (error) {
        console.error('Error loading dashboard data:', error);
        
        // Set fallback mock data
        setStats({ users: 150, courses: 25, enrollments: 320 });
        
        setUserCourses([
          { 
            courseId: 'WEB101', 
            courseTitle: 'Web Development Fundamentals', 
            progress: 75, 
            status: 'active',
            enrolledDate: '2024-01-15'
          },
          { 
            courseId: 'DS201', 
            courseTitle: 'Data Science with Python', 
            progress: 45, 
            status: 'active',
            enrolledDate: '2024-02-01'
          }
        ]);
        
        setAvailableCourses([
          { 
            courseId: 'AI301', 
            title: 'Machine Learning Fundamentals', 
            description: 'Introduction to AI and ML', 
            category: 'AI/ML', 
            level: 'Advanced' 
          },
          { 
            courseId: 'REA201', 
            title: 'React for Beginners', 
            description: 'Learn React framework', 
            category: 'Web Development', 
            level: 'Intermediate' 
          },
          { 
            courseId: 'PYT101', 
            title: 'Python Programming', 
            description: 'Python from basics to advanced', 
            category: 'Programming', 
            level: 'Beginner' 
          }
        ]);
      } finally {
        setLoading(false);
      }
    };

    loadDashboardData();
  }, [currentUser.id]);

  // Handle course enrollment
  const handleEnroll = async (courseId, courseTitle) => {
    try {
      // Use the correct enrollment endpoint (port 5001 for AI backend)
      const result = await ApiService.enrollCourse(currentUser.id, courseId);
      
      if (result.success) {
        alert(`Successfully enrolled in ${courseTitle}!`);
        
        // Update local state
        setUserCourses(prev => [...prev, {
          courseId,
          courseTitle,
          progress: 0,
          status: 'active',
          enrolledDate: new Date().toISOString().split('T')[0]
        }]);
        
        // Update stats
        setStats(prev => ({
          ...prev,
          enrollments: prev.enrollments + 1
        }));
      } else {
        alert(`Failed to enroll: ${result.error || 'Unknown error'}`);
      }
    } catch (error) {
      console.error('Enrollment error:', error);
      alert('Failed to enroll. Please try again.');
    }
  };

  // Handle progress update
  const handleProgress = async (courseId, courseTitle) => {
    const newProgress = Math.min(100, (userCourses.find(c => c.courseId === courseId)?.progress || 0) + 10);
    
    // Update local state
    setUserCourses(prev => 
      prev.map(course => 
        course.courseId === courseId 
          ? { ...course, progress: newProgress }
          : course
      )
    );
    
    alert(`Progress updated for ${courseTitle}: ${newProgress}%`);
  };

  // Debug function to test APIs
  const testApis = async () => {
    console.log('=== Testing APIs ===');
    
    // Test enrollments
    const enrollments = await ApiService.getUserEnrollments(currentUser.id);
    console.log('User Enrollments:', enrollments);
    
    // Test all enrollments
    const allEnrollments = await ApiService.getEnrollments();
    console.log('All Enrollments:', allEnrollments);
    
    // Test stats
    const stats = await ApiService.getStats();
    console.log('Stats:', stats);
    
    // Test analytics
    const analytics = await ApiService.getAnalyticsDashboard();
    console.log('Analytics:', analytics);
    
    // Test courses from analytics
    const courses = await ApiService.getCoursesFromAnalytics();
    console.log('Courses from Analytics:', courses);
    
    // Test AI backend courses
    const aiCourses = await ApiService.getCourses();
    console.log('AI Courses:', aiCourses);
    
    // Test user stats
    const userStats = await ApiService.getUserStats(currentUser.id);
    console.log('User Stats:', userStats);
  };

  if (loading) {
    return (
      <div className="container mt-4 text-center">
        <div className="spinner-border text-primary" role="status">
          <span className="visually-hidden">Loading...</span>
        </div>
        <p className="mt-2">Loading your dashboard...</p>
        <button className="btn btn-sm btn-outline-secondary mt-2" onClick={testApis}>
          Test APIs
        </button>
      </div>
    );
  }

  return (
    <div className="container mt-4">
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h1 className="mb-0">
          <i className="fas fa-graduation-cap me-2"></i>
          Learning Dashboard
        </h1>
        <button className="btn btn-sm btn-outline-info" onClick={testApis}>
          <i className="fas fa-bug me-1"></i>
          Debug APIs
        </button>
      </div>
      
      {/* Welcome Message */}
      <div className="alert alert-info mb-4">
        <div className="d-flex align-items-center">
          <i className="fas fa-user-circle fa-2x me-3"></i>
          <div>
            <h5 className="mb-1">Welcome back, {currentUser.name}!</h5>
            <p className="mb-0">User ID: {currentUser.id}  Email: {currentUser.email}</p>
          </div>
        </div>
      </div>
      
      {/* Stats Overview */}
      <div className="row mb-4">
        <div className="col-md-3 mb-3">
          <div className="card text-center shadow-sm">
            <div className="card-body">
              <h1 className="display-4 text-primary">{stats.users || 0}</h1>
              <p className="card-text">Total Users</p>
            </div>
          </div>
        </div>
        <div className="col-md-3 mb-3">
          <div className="card text-center shadow-sm">
            <div className="card-body">
              <h1 className="display-4 text-success">{stats.courses || 0}</h1>
              <p className="card-text">Total Courses</p>
            </div>
          </div>
        </div>
        <div className="col-md-3 mb-3">
          <div className="card text-center shadow-sm">
            <div className="card-body">
              <h1 className="display-4 text-warning">{stats.enrollments || 0}</h1>
              <p className="card-text">Total Enrollments</p>
            </div>
          </div>
        </div>
        <div className="col-md-3 mb-3">
          <div className="card text-center shadow-sm">
            <div className="card-body">
              <h1 className="display-4 text-info">{userCourses.length}</h1>
              <p className="card-text">My Enrollments</p>
            </div>
          </div>
        </div>
      </div>

      {/* My Courses Section */}
      <div className="card mb-4 shadow-sm">
        <div className="card-header bg-primary text-white d-flex justify-content-between align-items-center">
          <h4 className="mb-0">
            <i className="fas fa-book-open me-2"></i>
            My Courses ({userCourses.length})
          </h4>
          <small>User ID: {currentUser.id}</small>
        </div>
        <div className="card-body">
          {userCourses.length === 0 ? (
            <div className="text-center py-5">
              <i className="fas fa-book fa-3x text-muted mb-3"></i>
              <h5>You haven't enrolled in any courses yet.</h5>
              <p className="text-muted">Browse available courses below to get started!</p>
              <a href="/courses" className="btn btn-primary">
                <i className="fas fa-search me-2"></i>
                Browse Courses
              </a>
            </div>
          ) : (
            <div className="table-responsive">
              <table className="table table-hover">
                <thead>
                  <tr>
                    <th>Course ID</th>
                    <th>Course Title</th>
                    <th>Progress</th>
                    <th>Status</th>
                    <th>Enrolled</th>
                    <th>Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {userCourses.map((course, index) => (
                    <tr key={index}>
                      <td>
                        <code>{course.courseId}</code>
                      </td>
                      <td>
                        <strong>{course.courseTitle}</strong>
                      </td>
                      <td>
                        <div className="d-flex align-items-center">
                          <div className="progress flex-grow-1 me-2" style={{ height: '20px' }}>
                            <div 
                              className={`progress-bar ${course.progress === 100 ? 'bg-success' : 'bg-primary'}`}
                              role="progressbar" 
                              style={{ width: `${course.progress}%` }}
                              aria-valuenow={course.progress}
                              aria-valuemin="0"
                              aria-valuemax="100"
                            >
                              {course.progress}%
                            </div>
                          </div>
                          <span>{course.progress}%</span>
                        </div>
                      </td>
                      <td>
                        <span className={`badge bg-${course.status === 'completed' ? 'success' : course.status === 'active' ? 'info' : 'secondary'}`}>
                          {course.status}
                        </span>
                      </td>
                      <td>
                        <small>{course.enrolledDate}</small>
                      </td>
                      <td>
                        <button 
                          className="btn btn-sm btn-success me-2"
                          onClick={() => handleProgress(course.courseId, course.courseTitle)}
                          disabled={course.progress === 100}
                        >
                          <i className="fas fa-plus me-1"></i>
                          Update Progress
                        </button>
                        {course.progress === 100 && (
                          <span className="badge bg-success">
                            <i className="fas fa-check me-1"></i>
                            Completed
                          </span>
                        )}
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}
        </div>
      </div>

      {/* Available Courses Section */}
      <div className="card shadow-sm mb-4">
        <div className="card-header bg-success text-white">
          <h4 className="mb-0">
            <i className="fas fa-bookmark me-2"></i>
            Available Courses ({availableCourses.length})
          </h4>
        </div>
        <div className="card-body">
          {availableCourses.length === 0 ? (
            <div className="alert alert-warning">
              <i className="fas fa-exclamation-triangle me-2"></i>
              No courses available. Please check backend connection.
            </div>
          ) : (
            <div className="row">
              {availableCourses.map((course, index) => {
                const isEnrolled = userCourses.some(c => c.courseId === course.courseId);
                
                return (
                  <div className="col-md-4 mb-3" key={index}>
                    <div className="card h-100 shadow-sm">
                      <div className="card-body d-flex flex-column">
                        <div className="mb-2">
                          <span className="badge bg-secondary">{course.courseId}</span>
                        </div>
                        <h6 className="card-title">{course.title}</h6>
                        <p className="card-text small text-muted flex-grow-1">
                          {course.description?.substring(0, 80)}...
                        </p>
                        <div className="mb-3">
                          <span className="badge bg-primary me-1">{course.category}</span>
                          <span className="badge bg-secondary me-1">{course.level}</span>
                          {course.enrolledCount > 0 && (
                            <span className="badge bg-info">
                              <i className="fas fa-users me-1"></i>
                              {course.enrolledCount}
                            </span>
                          )}
                        </div>
                        <button 
                          className="btn btn-primary w-100 mt-auto"
                          onClick={() => handleEnroll(course.courseId, course.title)}
                          disabled={isEnrolled}
                        >
                          <i className="fas fa-plus-circle me-2"></i>
                          {isEnrolled ? 'Already Enrolled' : 'Enroll Now'}
                        </button>
                      </div>
                    </div>
                  </div>
                );
              })}
            </div>
          )}
        </div>
      </div>

      {/* Backend Info */}
      <div className="card">
        <div className="card-header bg-light">
          <h5 className="mb-0">
            <i className="fas fa-server me-2"></i>
            Backend Information
          </h5>
        </div>
        <div className="card-body">
          <div className="row">
            <div className="col-md-6">
              <div className="alert alert-secondary">
                <h6><i className="fas fa-database me-2"></i>Analytics Backend</h6>
                <p className="mb-1"><strong>Port:</strong> 5000 (MongoDB)</p>
                <p className="mb-1"><strong>Used for:</strong> Users, Enrollments, Statistics</p>
                <p className="mb-0"><strong>Courses endpoint:</strong> /api/courses</p>
              </div>
            </div>
            <div className="col-md-6">
              <div className="alert alert-info">
                <h6><i className="fas fa-robot me-2"></i>AI Backend</h6>
                <p className="mb-1"><strong>Port:</strong> 5001 (AI/ML)</p>
                <p className="mb-1"><strong>Used for:</strong> Course content, AI chat, Recommendations</p>
                <p className="mb-0"><strong>Enrollment endpoint:</strong> /enroll</p>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
