import React, { useState, useEffect } from 'react';

function DatabaseStats() {
  const [stats, setStats] = useState({
    users: 0,
    courses: 0,
    enrollments: 0,
    loading: true
  });
  const [recentEnrollments, setRecentEnrollments] = useState([]);
  const [topCourses, setTopCourses] = useState([]);
  const [userDistribution, setUserDistribution] = useState({});
  const [enrollmentStatus, setEnrollmentStatus] = useState({});
  const [categories, setCategories] = useState([]);

  useEffect(() => {
    loadDatabaseStats();
  }, []);

  const loadDatabaseStats = async () => {
    setStats(prev => ({ ...prev, loading: true }));
    
    try {
      // Load analytics summary
      const summaryResponse = await fetch('http://localhost:5000/api/analytics/summary');
      const summaryData = await summaryResponse.json();
      
      if (summaryData.success) {
        setStats({
          users: summaryData.data.totals.users,
          courses: summaryData.data.totals.courses,
          enrollments: summaryData.data.totals.enrollments,
          loading: false
        });
        
        setRecentEnrollments(summaryData.data.recentEnrollments || []);
        setTopCourses(summaryData.data.topCourses || []);
        setUserDistribution(summaryData.data.distribution?.users || {});
        setEnrollmentStatus(summaryData.data.distribution?.enrollmentStatus || []);
        setCategories(summaryData.data.distribution?.categories || []);
      }
    } catch (error) {
      console.error('Failed to load database stats:', error);
      
      // Fallback to simple stats
      try {
        const simpleStats = await fetch('http://localhost:5000/api/stats');
        const simpleData = await simpleStats.json();
        if (simpleData.success) {
          setStats({
            users: simpleData.data.users,
            courses: simpleData.data.courses,
            enrollments: simpleData.data.enrollments,
            loading: false
          });
        }
      } catch (e) {
        console.error('Fallback also failed:', e);
        setStats(prev => ({ ...prev, loading: false }));
      }
    }
  };

  const getEnrollmentStatusColor = (status) => {
    switch(status) {
      case 'active': return 'primary';
      case 'completed': return 'success';
      case 'dropped': return 'danger';
      default: return 'secondary';
    }
  };

  if (stats.loading) {
    return (
      <div className="card">
        <div className="card-body text-center">
          <div className="spinner-border text-primary" role="status">
            <span className="visually-hidden">Loading database stats...</span>
          </div>
          <p className="mt-2">Loading database statistics...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="card shadow-lg">
      <div className="card-header bg-primary text-white">
        <h5 className="card-title mb-0">üìä Database Statistics</h5>
      </div>
      <div className="card-body">
        
        {/* Quick Stats Row */}
        <div className="row mb-4">
          <div className="col-md-4">
            <div className="card bg-light">
              <div className="card-body text-center">
                <h3 className="text-primary">{stats.users}</h3>
                <p className="card-text">Total Users</p>
                <small className="text-muted">Including students, instructors, admins</small>
              </div>
            </div>
          </div>
          <div className="col-md-4">
            <div className="card bg-light">
              <div className="card-body text-center">
                <h3 className="text-success">{stats.courses}</h3>
                <p className="card-text">Courses Available</p>
                <small className="text-muted">Across different categories</small>
              </div>
            </div>
          </div>
          <div className="col-md-4">
            <div className="card bg-light">
              <div className="card-body text-center">
                <h3 className="text-warning">{stats.enrollments}</h3>
                <p className="card-text">Total Enrollments</p>
                <small className="text-muted">Active and completed</small>
              </div>
            </div>
          </div>
        </div>

        {/* User Distribution */}
        {userDistribution && (
          <div className="mb-4">
            <h6>üë• User Distribution</h6>
            <div className="row">
              <div className="col-4 text-center">
                <div className="p-3 border rounded">
                  <div className="text-primary fs-4">{userDistribution.students || 0}</div>
                  <small className="text-muted">Students</small>
                </div>
              </div>
              <div className="col-4 text-center">
                <div className="p-3 border rounded">
                  <div className="text-warning fs-4">{userDistribution.instructors || 0}</div>
                  <small className="text-muted">Instructors</small>
                </div>
              </div>
              <div className="col-4 text-center">
                <div className="p-3 border rounded">
                  <div className="text-danger fs-4">{userDistribution.admins || 0}</div>
                  <small className="text-muted">Admins</small>
                </div>
              </div>
            </div>
          </div>
        )}

        {/* Enrollment Status */}
        {enrollmentStatus.length > 0 && (
          <div className="mb-4">
            <h6>üéØ Enrollment Status</h6>
            <div className="row">
              {enrollmentStatus.map((item, index) => (
                <div className="col-4" key={index}>
                  <div className="p-3 border rounded text-center">
                    <div className={`fs-4 text-${getEnrollmentStatusColor(item._id)}`}>
                      {item.count}
                    </div>
                    <small className="text-muted">{item._id}</small>
                  </div>
                </div>
              ))}
            </div>
          </div>
        )}

        {/* Top Courses */}
        {topCourses.length > 0 && (
          <div className="mb-4">
            <h6>üéØ Top Courses by Enrollment</h6>
            <div className="list-group">
              {topCourses.map((course, index) => (
                <div key={index} className="list-group-item d-flex justify-content-between align-items-center">
                  <div>
                    <strong>{course.title}</strong>
                    <div className="small text-muted">{course.category} ‚Ä¢ {course.level}</div>
                  </div>
                  <span className="badge bg-primary rounded-pill">
                    {course.enrolledStudents} enrolled
                  </span>
                </div>
              ))}
            </div>
          </div>
        )}

        {/* Course Categories */}
        {categories.length > 0 && (
          <div className="mb-4">
            <h6>üìö Course Categories</h6>
            <div className="list-group">
              {categories.map((category, index) => (
                <div key={index} className="list-group-item d-flex justify-content-between align-items-center">
                  <div>
                    <strong>{category._id}</strong>
                  </div>
                  <div>
                    <span className="badge bg-secondary me-2">
                      {category.count} courses
                    </span>
                    <span className="badge bg-info">
                      {category.totalEnrolled} students
                    </span>
                  </div>
                </div>
              ))}
            </div>
          </div>
        )}

        {/* Recent Enrollments */}
        {recentEnrollments.length > 0 && (
          <div className="mb-4">
            <h6>üìù Recent Enrollments</h6>
            <div className="table-responsive">
              <table className="table table-sm">
                <thead>
                  <tr>
                    <th>User</th>
                    <th>Course</th>
                    <th>Status</th>
                    <th>Progress</th>
                  </tr>
                </thead>
                <tbody>
                  {recentEnrollments.map((enrollment, index) => (
                    <tr key={index}>
                      <td>{enrollment.userName}</td>
                      <td>{enrollment.courseTitle}</td>
                      <td>
                        <span className={`badge bg-${getEnrollmentStatusColor(enrollment.status)}`}>
                          {enrollment.status}
                        </span>
                      </td>
                      <td>
                        <div className="progress" style={{ height: '5px' }}>
                          <div 
                            className="progress-bar" 
                            style={{ width: `${enrollment.progress}%` }}
                          ></div>
                        </div>
                        <small>{enrollment.progress}%</small>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        )}

        {/* Quick Links */}
        <div className="mt-4 pt-3 border-top">
          <h6>üîó Quick Database Links</h6>
          <div className="d-flex flex-wrap gap-2">
            <a 
              href="http://localhost:5000/api/users" 
              target="_blank" 
              rel="noopener noreferrer"
              className="btn btn-outline-primary btn-sm"
            >
              View Users API
            </a>
            <a 
              href="http://localhost:5000/api/courses" 
              target="_blank" 
              rel="noopener noreferrer"
              className="btn btn-outline-success btn-sm"
            >
              View Courses API
            </a>
            <a 
              href="http://localhost:5000/api/enrollments" 
              target="_blank" 
              rel="noopener noreferrer"
              className="btn btn-outline-warning btn-sm"
            >
              View Enrollments API
            </a>
            <button 
              className="btn btn-outline-info btn-sm"
              onClick={loadDatabaseStats}
            >
              üîÑ Refresh Data
            </button>
          </div>
        </div>
      </div>
      <div className="card-footer text-muted text-center">
        <small>Database updated: {new Date().toLocaleTimeString()}</small>
      </div>
    </div>
  );
}

export default DatabaseStats;
