import React, { useState } from 'react';

function PasswordStrength() {
  const [password, setPassword] = useState('');
  const [strengthData, setStrengthData] = useState(null);

  const getPasswordStrengthData = (pass) => {
    const requirements = {
      length: pass.length >= 8,
      uppercase: /[A-Z]/.test(pass),
      lowercase: /[a-z]/.test(pass),
      number: /\d/.test(pass),
      special: /[!@#$%^&*(),.?":{}|<>]/.test(pass)
    };

    const score = Object.values(requirements).filter(Boolean).length;
    
    let strength = 'Weak';
    let color = 'danger';
    
    if (score >= 4) {
      strength = 'Strong';
      color = 'success';
    } else if (score >= 3) {
      strength = 'Medium';
      color = 'warning';
    }
    
    return { strength, color, requirements, score };
  };

  const handlePasswordChange = (e) => {
    const value = e.target.value;
    setPassword(value);
    setStrengthData(getPasswordStrengthData(value));
  };

  return (
    <div className="card">
      <div className="card-body">
        <h5 className="card-title">Password Strength Checker</h5>
        <div className="mb-3">
          <label className="form-label">Enter a password to check its strength:</label>
          <input
            type="password"
            className="form-control"
            value={password}
            onChange={handlePasswordChange}
            placeholder="Enter password"
          />
        </div>
        
        {password && strengthData && (
          <div>
            <div className="progress mb-2">
              <div 
                className={`progress-bar bg-${strengthData.color}`}
                style={{ width: `${(strengthData.score / 5) * 100}%` }}
              ></div>
            </div>
            <p className={`text-${strengthData.color} fw-bold`}>
              Password Strength: {strengthData.strength}
            </p>
            <div className="small">
              <p className="mb-1">Requirements:</p>
              <ul className="list-unstyled">
                <li>
                  {strengthData.requirements.length ? '✅' : '❌'} 
                  At least 8 characters
                </li>
                <li>
                  {strengthData.requirements.uppercase ? '✅' : '❌'} 
                  Uppercase letter
                </li>
                <li>
                  {strengthData.requirements.lowercase ? '✅' : '❌'} 
                  Lowercase letter
                </li>
                <li>
                  {strengthData.requirements.number ? '✅' : '❌'} 
                  Number
                </li>
                <li>
                  {strengthData.requirements.special ? '✅' : '❌'} 
                  Special character
                </li>
              </ul>
            </div>
          </div>
        )}
        
        {!password && (
          <div className="alert alert-info">
            <small>Start typing a password to see its strength analysis</small>
          </div>
        )}
      </div>
    </div>
  );
}

export default PasswordStrength;
