import React, { useState, useEffect } from 'react';
import ApiService from '../services/api';

const Dashboard = () => {
  const [stats, setStats] = useState({ users: 0, courses: 0, enrollments: 0 });
  const [recentEnrollments, setRecentEnrollments] = useState([]);

  useEffect(() => {
    const loadDashboardData = async () => {
      try {
        // 1. Load Stats - USE CORRECT PATTERN
        const statsResponse = await ApiService.getStats();
        if (statsResponse && statsResponse.success) {
          setStats(statsResponse.data); // ✅ Extract .data
        }

        // 2. Load Enrollments - APPLY SAME PATTERN
        const enrollResponse = await ApiService.getEnrollments();
        if (enrollResponse && enrollResponse.success) {
          // Assuming enrollResponse.data is an array
          setRecentEnrollments(enrollResponse.data.slice(0, 5));
        }
      } catch (error) {
        console.error('Failed to load dashboard:', error);
      }
    };
    loadDashboardData();
  }, []);

  return (
    <div>
      <h2>Dashboard</h2>
      <div className="stats-grid">
        <div className="stat-card">
          <h3>Total Users</h3>
          {/* This will now show 10, not 0 */}
          <p className="count">{stats.users}</p>
        </div>
        <div className="stat-card">
          <h3>Total Courses</h3>
          <p className="count">{stats.courses}</p>
        </div>
        {/* ... */}
      </div>
    </div>
  );
};
export default Dashboard;
