import React, { useState, useEffect } from 'react';
import ApiService from '../services/api';

const AdminDashboard = () => {
  const [stats, setStats] = useState({ users: 0, courses: 0, enrollments: 0 });
  const [allUsers, setAllUsers] = useState([]);
  const [allCourses, setAllCourses] = useState([]);
  const [allEnrollments, setAllEnrollments] = useState([]);
  const [loading, setLoading] = useState(true);

// In your AdminDashboard.js, update the useEffect:
useEffect(() => {
  const loadAdminData = async () => {
    try {
      setLoading(true);
      console.log('üîç DEBUG: Starting to load admin data...');
      
      // Load stats from MongoDB (Port 5000)
      const statsResponse = await ApiService.getStats();
      console.log('üîç DEBUG: Stats API Response:', statsResponse);
      console.log('üîç DEBUG: statsResponse.success:', statsResponse?.success);
      console.log('üîç DEBUG: statsResponse.data:', statsResponse?.data);
      
      if (statsResponse && statsResponse.success) {
        console.log('‚úÖ DEBUG: Setting stats to:', statsResponse.data);
        setStats(statsResponse.data);
      } else {
        console.error('‚ùå DEBUG: Stats response not successful or missing data');
        // Set fallback stats based on table data
        setStats({
          users: allUsers.length,
          courses: allCourses.length,
          enrollments: allEnrollments.length
        });
      }
      
      // Load users from MongoDB
      const usersResponse = await ApiService.getUsers();
      console.log('üîç DEBUG: Users API Response:', usersResponse);
      if (usersResponse && usersResponse.success) {
        setAllUsers(usersResponse.data || []);
      }
      
      // Load courses from MongoDB (Port 5000)
      const coursesResponse = await ApiService.getCoursesFromAnalytics();
      console.log('üîç DEBUG: Courses API Response:', coursesResponse);
      if (coursesResponse && coursesResponse.success) {
        setAllCourses(coursesResponse.data || []);
      }
      
      // Load enrollments from MongoDB
      const enrollmentsResponse = await ApiService.getEnrollments();
      console.log('üîç DEBUG: Enrollments API Response:', enrollmentsResponse);
      if (enrollmentsResponse && enrollmentsResponse.success) {
        setAllEnrollments(enrollmentsResponse.data || []);
      }
      
    } catch (error) {
      console.error('‚ùå Error loading admin data:', error);
    } finally {
      setLoading(false);
    }
  };
  
  loadAdminData();
}, []);

  if (loading) {
    return (
      <div className="container mt-4 text-center">
        <div className="spinner-border" role="status">
          <span className="visually-hidden">Loading...</span>
        </div>
        <p className="mt-2">Loading admin dashboard...</p>
      </div>
    );
  }

  return (
    <div className="container mt-4">
      <h1 className="mb-4">Admin Dashboard</h1>
      
      {/* Stats Cards */}
      <div className="row mb-4">
        <div className="col-md-3">
          <div className="card text-white bg-primary">
            <div className="card-body">
              <h5 className="card-title">Total Users</h5>
              <p className="card-text display-4">{stats.users}</p>
              <small>From MongoDB</small>
            </div>
          </div>
        </div>
        <div className="col-md-3">
          <div className="card text-white bg-success">
            <div className="card-body">
              <h5 className="card-title">Total Courses</h5>
              <p className="card-text display-4">{stats.courses}</p>
              <small>From MongoDB</small>
            </div>
          </div>
        </div>
        <div className="col-md-3">
          <div className="card text-white bg-info">
            <div className="card-body">
              <h5 className="card-title">Total Enrollments</h5>
              <p className="card-text display-4">{stats.enrollments}</p>
              <small>From MongoDB</small>
            </div>
          </div>
        </div>
        <div className="col-md-3">
          <div className="card text-white bg-warning">
            <div className="card-body">
              <h5 className="card-title">Database</h5>
              <p className="card-text display-6">MongoDB</p>
              <small>Connected ‚úì</small>
            </div>
          </div>
        </div>
      </div>

      {/* Users Table */}
      <div className="card mb-4">
        <div className="card-body">
          <h3>Users ({allUsers.length})</h3>
          <table className="table table-striped">
            <thead>
              <tr>
                <th>Name</th>
                <th>Email</th>
                <th>Role</th>
                <th>Enrolled Courses</th>
                <th>Joined</th>
              </tr>
            </thead>
            <tbody>
              {allUsers.map(user => (
                <tr key={user._id}>
                  <td>{user.name}</td>
                  <td>{user.email}</td>
                  <td><span className="badge bg-info">{user.role}</span></td>
                  <td>{user.enrolledCourses}</td>
                  <td>{new Date(user.joinDate).toLocaleDateString()}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>

      {/* Courses Table */}
      <div className="card mb-4">
        <div className="card-body">
          <h3>Courses ({allCourses.length})</h3>
          <table className="table table-striped">
            <thead>
              <tr>
                <th>Course ID</th>
                <th>Title</th>
                <th>Category</th>
                <th>Instructor</th>
                <th>Enrolled</th>
                <th>Rating</th>
              </tr>
            </thead>
            <tbody>
              {allCourses.map(course => (
                <tr key={course._id}>
                  <td><code>{course.courseId}</code></td>
                  <td>{course.title}</td>
                  <td><span className="badge bg-secondary">{course.category}</span></td>
                  <td>{course.instructor}</td>
                  <td>{course.enrolledStudents}/{course.maxStudents}</td>
                  <td>{course.rating} ‚≠ê</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>

      {/* Enrollments Table */}
      <div className="card">
        <div className="card-body">
          <h3>Recent Enrollments ({allEnrollments.length})</h3>
          <table className="table table-striped">
            <thead>
              <tr>
                <th>User</th>
                <th>Course</th>
                <th>Enrolled Date</th>
                <th>Status</th>
              </tr>
            </thead>
            <tbody>
              {allEnrollments.slice(0, 10).map(enrollment => (
                <tr key={enrollment._id}>
                  <td>{enrollment.userName}</td>
                  <td>{enrollment.courseTitle}</td>
                  <td>{new Date(enrollment.enrolledDate).toLocaleDateString()}</td>
                  <td><span className="badge bg-success">Active</span></td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default AdminDashboard;
