import React, { useState, useEffect, useRef } from 'react';
import './AIChatbot.css'; // We'll create this CSS

const AIChatbot = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [conversationStarters, setConversationStarters] = useState([]);
  const [chatHistory, setChatHistory] = useState([]);
  const [userId] = useState(`user_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`);
  const [aiProvider, setAiProvider] = useState('auto'); // 'auto', 'openai', 'gemini', 'deepseek'
  
  const messagesEndRef = useRef(null);
  const chatContainerRef = useRef(null);

  // Initial greeting
  const initialMessages = [
    {
      id: 1,
      text: "Hello! I'm your AI Learning Assistant. I can help you find courses, explain concepts, and guide your learning journey. How can I help you today?",
      sender: 'bot',
      timestamp: new Date(),
      type: 'greeting',
      provider: 'ai'
    }
  ];

  // Initialize
  useEffect(() => {
    setMessages(initialMessages);
    fetchConversationStarters();
    loadChatHistory();
  }, []);

  // Scroll to bottom
  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  // Fetch conversation starters
  const fetchConversationStarters = async () => {
    try {
      // Use the new ApiService or direct fetch
      const response = await fetch('http://localhost:5001/api/hybrid-ai/health');
      if (response.ok) {
        // Default starters since we don't have a specific endpoint
        setConversationStarters([
          "What courses do you have?",
          "Explain web development",
          "How do I become a data scientist?",
          "Recommend beginner courses",
          "What is React?",
          "Tell me about Python programming",
          "Help me with machine learning"
        ]);
      }
    } catch (error) {
      console.error('Failed to fetch starters:', error);
      // Default starters
      setConversationStarters([
        "What courses do you have?",
        "Explain web development",
        "How do I become a data scientist?",
        "Recommend beginner courses"
      ]);
    }
  };

  // Load chat history (demo - in production use user authentication)
  const loadChatHistory = async () => {
    try {
      // Try to load from localStorage for demo
      const savedHistory = localStorage.getItem(`chat_history_${userId}`);
      if (savedHistory) {
        const history = JSON.parse(savedHistory);
        setChatHistory(history.slice(-10)); // Last 10 messages
      }
    } catch (error) {
      console.error('Failed to load history:', error);
    }
  };

  // Save chat message to localStorage
  const saveChatMessage = async (message, response, provider) => {
    try {
      const chatEntry = {
        userId,
        message,
        response,
        provider,
        timestamp: new Date().toISOString()
      };
      
      // Save to localStorage for demo
      const existing = JSON.parse(localStorage.getItem(`chat_history_${userId}`) || '[]');
      existing.push(chatEntry);
      localStorage.setItem(`chat_history_${userId}`, JSON.stringify(existing.slice(-50))); // Keep last 50
      
      // Also save to chatHistory state
      setChatHistory(prev => [...prev, chatEntry].slice(-10));
    } catch (error) {
      console.error('Failed to save chat:', error);
    }
  };

  // Send message to AI backend (PORT 5001)
  const sendMessage = async (messageText = input) => {
    if (!messageText.trim() || isLoading) return;

    const userMessage = {
      id: Date.now(),
      text: messageText,
      sender: 'user',
      timestamp: new Date(),
      provider: 'user'
    };

    setMessages(prev => [...prev, userMessage]);
    setInput('');
    setIsLoading(true);

    try {
      console.log(`ğŸ¤– Sending to AI backend (Port 5001): ${messageText}`);
      
      const response = await fetch('http://localhost:5001/api/hybrid-ai/chat', {
        method: 'POST',
        headers: { 
          'Content-Type': 'application/json',
          'Accept': 'application/json'
        },
        body: JSON.stringify({ 
          message: messageText,
          provider: aiProvider // 'auto', 'openai', 'gemini', 'deepseek'
        })
      });

      console.log('ğŸ“¡ Response status:', response.status);
      
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      
      const data = await response.json();
      console.log('âœ… AI Response:', data);
      
      if (data.success) {
        const botMessage = {
          id: Date.now() + 1,
          text: data.response,
          sender: 'bot',
          timestamp: new Date(),
          type: 'ai_response',
          provider: data.provider || 'ai',
          suggestions: data.suggestions || []
        };

        setMessages(prev => [...prev, botMessage]);
        saveChatMessage(messageText, data.response, data.provider || 'ai');
        setAiProvider(data.provider || 'auto');
      } else {
        throw new Error(data.error || 'AI chat failed');
      }
    } catch (error) {
      console.error('âŒ Chat error:', error);
      
      const errorMessage = {
        id: Date.now() + 1,
        text: "I apologize, but I'm having trouble connecting to the AI service. Please make sure the AI backend is running on port 5001.",
        sender: 'bot',
        timestamp: new Date(),
        type: 'error',
        provider: 'error'
      };

      setMessages(prev => [...prev, errorMessage]);
    } finally {
      setIsLoading(false);
    }
  };

  // Test AI connection directly
  const testAIConnection = async () => {
    setIsLoading(true);
    try {
      const testMessage = {
        id: Date.now(),
        text: "Testing AI connection...",
        sender: 'bot',
        timestamp: new Date(),
        type: 'test'
      };
      setMessages(prev => [...prev, testMessage]);

      const response = await fetch('http://localhost:5001/api/hybrid-ai/chat', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ message: 'Hello, are you working?' })
      });

      const data = await response.json();
      
      if (data.success) {
        const successMessage = {
          id: Date.now() + 1,
          text: `âœ… AI Connection Successful! Using ${data.provider || 'auto'} provider. Response: "${data.response.substring(0, 50)}..."`,
          sender: 'bot',
          timestamp: new Date(),
          type: 'success',
          provider: data.provider
        };
        setMessages(prev => [...prev, successMessage]);
      } else {
        throw new Error('AI test failed');
      }
    } catch (error) {
      console.error('Test failed:', error);
      const errorMessage = {
        id: Date.now() + 1,
        text: "âŒ AI Connection Failed. Please check: 1) Port 5001 is running, 2) AI backend is started, 3) No firewall blocking",
        sender: 'bot',
        timestamp: new Date(),
        type: 'error'
      };
      setMessages(prev => [...prev, errorMessage]);
    } finally {
      setIsLoading(false);
    }
  };

  // Handle quick starter click
  const handleStarterClick = (starter) => {
    setInput(starter);
    setTimeout(() => {
      sendMessage(starter);
    }, 100);
  };

  // Handle key press
  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      sendMessage();
    }
  };

  // Scroll to bottom
  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  // Clear conversation
  const clearConversation = () => {
    setMessages(initialMessages);
  };

  // Format time
  const formatTime = (date) => {
    return date.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
  };

  // Toggle chat window
  const toggleChat = () => {
    setIsOpen(!isOpen);
  };

  // Change AI provider
  const changeProvider = (provider) => {
    setAiProvider(provider);
    const providerMessage = {
      id: Date.now(),
      text: `Switched to ${provider.toUpperCase()} AI provider`,
      sender: 'bot',
      timestamp: new Date(),
      type: 'info'
    };
    setMessages(prev => [...prev, providerMessage]);
  };

  // Render message with formatting
  const renderMessage = (message) => {
    const isBot = message.sender === 'bot';
    
    return (
      <div className={`message-container ${isBot ? 'bot-message' : 'user-message'}`} key={message.id}>
        <div className="message-header">
          <div className="message-sender">
            {isBot ? (
              <>
                <span className="bot-icon">ğŸ¤–</span>
                <span className="sender-name">
                  AI Assistant {message.provider && message.provider !== 'ai' && `â€¢ ${message.provider.toUpperCase()}`}
                </span>
              </>
            ) : (
              <>
                <span className="user-icon">ğŸ‘¤</span>
                <span className="sender-name">You</span>
              </>
            )}
          </div>
          <div className="message-time">{formatTime(new Date(message.timestamp))}</div>
        </div>
        
        <div className={`message-bubble ${isBot ? 'bot-bubble' : 'user-bubble'}`}>
          <div className="message-text">
            {message.text.split('\n').map((line, i) => (
              <React.Fragment key={i}>
                {line}
                {i < message.text.split('\n').length - 1 && <br />}
              </React.Fragment>
            ))}
          </div>
        </div>

        {/* Suggestions for bot messages */}
        {isBot && message.suggestions && message.suggestions.length > 0 && (
          <div className="message-suggestions">
            <small className="suggestions-label">Quick follow-ups:</small>
            <div className="suggestions-buttons">
              {message.suggestions.map((suggestion, index) => (
                <button
                  key={index}
                  className="suggestion-btn"
                  onClick={() => handleStarterClick(suggestion)}
                  disabled={isLoading}
                >
                  {suggestion}
                </button>
              ))}
            </div>
          </div>
        )}
      </div>
    );
  };

  return (
    <>
      {/* Chatbot Toggle Button */}
      <button
        className="chatbot-toggle-btn"
        onClick={toggleChat}
        title="AI Learning Assistant"
      >
        {isOpen ? (
          <span className="close-icon">âœ•</span>
        ) : (
          <>
            <span className="chat-icon">ğŸ’¬</span>
            <span className="notification-dot"></span>
          </>
        )}
      </button>

      {/* Chat Window */}
      {isOpen && (
        <div className="chatbot-window">
          {/* Header */}
          <div className="chat-header">
            <div className="chat-header-left">
              <div className="chat-avatar">
                <span className="avatar-icon">ğŸ¤–</span>
              </div>
              <div className="chat-info">
                <h3 className="chat-title">AI Learning Assistant</h3>
                <div className="chat-status">
                  <span className="status-dot online"></span>
                  <span className="status-text">
                    Connected to Port 5001 â€¢ Provider: {aiProvider.toUpperCase()}
                  </span>
                </div>
              </div>
            </div>
            <div className="chat-header-right">
              <div className="provider-selector">
                <select 
                  value={aiProvider}
                  onChange={(e) => changeProvider(e.target.value)}
                  className="form-select form-select-sm"
                  disabled={isLoading}
                >
                  <option value="auto">ğŸ¤– Auto (Smart)</option>
                  <option value="openai">âš¡ OpenAI</option>
                  <option value="gemini">ğŸ”® Gemini</option>
                  <option value="deepseek">ğŸŒŠ DeepSeek</option>
                </select>
              </div>
              <button 
                className="header-btn test-btn"
                onClick={testAIConnection}
                title="Test AI Connection"
                disabled={isLoading}
              >
                <span className="btn-icon">ğŸ”§</span>
              </button>
              <button 
                className="header-btn clear-btn"
                onClick={clearConversation}
                title="Clear conversation"
                disabled={isLoading}
              >
                <span className="btn-icon">ğŸ—‘ï¸</span>
              </button>
              <button 
                className="header-btn close-btn"
                onClick={() => setIsOpen(false)}
                title="Close chat"
              >
                <span className="btn-icon">âœ•</span>
              </button>
            </div>
          </div>

          {/* Chat Body */}
          <div className="chat-body" ref={chatContainerRef}>
            {/* Welcome Message */}
            <div className="welcome-section">
              <div className="welcome-card">
                <div className="welcome-icon">ğŸ‘‹</div>
                <h4 className="welcome-title">How can I help you today?</h4>
                <p className="welcome-text">
                  I'm connected to the AI backend on port 5001. Ask me about courses, 
                  programming, or learning advice!
                </p>
                <div className="welcome-actions">
                  <button 
                    className="btn btn-sm btn-primary"
                    onClick={testAIConnection}
                    disabled={isLoading}
                  >
                    {isLoading ? 'Testing...' : 'Test Connection'}
                  </button>
                  <small className="text-muted ms-2">
                    Current provider: <strong>{aiProvider.toUpperCase()}</strong>
                  </small>
                </div>
              </div>
            </div>

            {/* Conversation Starters */}
            {conversationStarters.length > 0 && (
              <div className="starters-section">
                <h5 className="starters-title">ğŸ’¡ Try asking:</h5>
                <div className="starters-grid">
                  {conversationStarters.map((starter, index) => (
                    <button
                      key={index}
                      className="starter-card"
                      onClick={() => handleStarterClick(starter)}
                      disabled={isLoading}
                    >
                      {starter}
                    </button>
                  ))}
                </div>
              </div>
            )}

            {/* Messages */}
            <div className="messages-container">
              {messages.map(renderMessage)}
              {isLoading && (
                <div className="typing-indicator">
                  <div className="typing-dots">
                    <div className="dot"></div>
                    <div className="dot"></div>
                    <div className="dot"></div>
                  </div>
                  <span className="typing-text">
                    AI is thinking... Using {aiProvider.toUpperCase()}
                  </span>
                </div>
              )}
              <div ref={messagesEndRef} />
            </div>
          </div>

          {/* Chat Footer */}
          <div className="chat-footer">
            <div className="input-container">
              <div className="input-wrapper">
                <textarea
                  className="chat-input"
                  placeholder={`Ask me anything (using ${aiProvider.toUpperCase()})...`}
                  value={input}
                  onChange={(e) => setInput(e.target.value)}
                  onKeyPress={handleKeyPress}
                  disabled={isLoading}
                  rows="1"
                />
                <div className="input-actions">
                  <button
                    className="send-btn"
                    onClick={() => sendMessage()}
                    disabled={isLoading || !input.trim()}
                    title="Send message"
                  >
                    {isLoading ? (
                      <span className="spinner"></span>
                    ) : (
                      <span className="send-icon">â¤</span>
                    )}
                  </button>
                </div>
              </div>
              
              <div className="input-hints">
                <small className="hint-text">
                  Press <kbd>Enter</kbd> to send â€¢ Current: {aiProvider.toUpperCase()} â€¢ Port: 5001
                </small>
              </div>
            </div>
          </div>
        </div>
      )}
    </>
  );
};

export default AIChatbot;
