import React, { useState, useEffect, useRef } from 'react';
import './AIChatbot.css'; // We'll create this CSS

const AIChatbot = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [conversationStarters, setConversationStarters] = useState([]);
  const [chatHistory, setChatHistory] = useState([]);
  const [userId] = useState(`user_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`);
  
  const messagesEndRef = useRef(null);
  const chatContainerRef = useRef(null);

  // Initial greeting
  const initialMessages = [
    {
      id: 1,
      text: "Hello! I'm your AI Learning Assistant. I can help you find courses, explain concepts, and guide your learning journey. How can I help you today?",
      sender: 'bot',
      timestamp: new Date(),
      type: 'greeting'
    }
  ];

  // Initialize
  useEffect(() => {
    setMessages(initialMessages);
    fetchConversationStarters();
    loadChatHistory();
  }, []);

  // Scroll to bottom
  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  // Fetch conversation starters
  const fetchConversationStarters = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/chatbot/starters');
      const data = await response.json();
      if (data.success) {
        setConversationStarters(data.starters);
      }
    } catch (error) {
      console.error('Failed to fetch starters:', error);
      // Default starters
      setConversationStarters([
        "What courses do you have?",
        "Explain web development",
        "How do I become a data scientist?",
        "Recommend beginner courses"
      ]);
    }
  };

  // Load chat history (demo - in production use user authentication)
  const loadChatHistory = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/chatbot/history', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ userId })
      });
      
      const data = await response.json();
      if (data.success && data.history.length > 0) {
        setChatHistory(data.history);
      }
    } catch (error) {
      console.error('Failed to load history:', error);
    }
  };

  // Save chat message
  const saveChatMessage = async (message, response, type) => {
    try {
      await fetch('http://localhost:5000/api/chatbot/save', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ userId, message, response, type })
      });
    } catch (error) {
      console.error('Failed to save chat:', error);
    }
  };

  // Send message to chatbot
  const sendMessage = async (messageText = input) => {
    if (!messageText.trim() || isLoading) return;

    const userMessage = {
      id: Date.now(),
      text: messageText,
      sender: 'user',
      timestamp: new Date()
    };

    setMessages(prev => [...prev, userMessage]);
    setInput('');
    setIsLoading(true);

    try {
      const response = await fetch('http://localhost:5000/api/chatbot/chat', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ 
          message: messageText,
          context: {
            userId,
            interests: ['learning', 'courses']
          }
        })
      });

      const data = await response.json();
      
      if (data.success) {
        const botMessage = {
          id: Date.now() + 1,
          text: data.response,
          sender: 'bot',
          timestamp: new Date(),
          type: data.type,
          suggestions: data.suggestions || [],
          data: data.data || null
        };

        setMessages(prev => [...prev, botMessage]);
        saveChatMessage(messageText, data.response, data.type);
      } else {
        throw new Error(data.error || 'Chat failed');
      }
    } catch (error) {
      console.error('Chat error:', error);
      
      const errorMessage = {
        id: Date.now() + 1,
        text: "I apologize, but I'm having trouble connecting right now. Please try again in a moment.",
        sender: 'bot',
        timestamp: new Date(),
        type: 'error'
      };

      setMessages(prev => [...prev, errorMessage]);
    } finally {
      setIsLoading(false);
    }
  };

  // Handle quick starter click
  const handleStarterClick = (starter) => {
    setInput(starter);
    setTimeout(() => {
      sendMessage(starter);
    }, 100);
  };

  // Handle key press
  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      sendMessage();
    }
  };

  // Scroll to bottom
  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  // Clear conversation
  const clearConversation = () => {
    setMessages(initialMessages);
  };

  // Format time
  const formatTime = (date) => {
    return date.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
  };

  // Toggle chat window
  const toggleChat = () => {
    setIsOpen(!isOpen);
  };

  // Render message with formatting
  const renderMessage = (message) => {
    const isBot = message.sender === 'bot';
    
    return (
      <div className={`message-container ${isBot ? 'bot-message' : 'user-message'}`} key={message.id}>
        <div className="message-header">
          <div className="message-sender">
            {isBot ? (
              <>
                <span className="bot-icon">ü§ñ</span>
                <span className="sender-name">AI Assistant</span>
              </>
            ) : (
              <>
                <span className="user-icon">üë§</span>
                <span className="sender-name">You</span>
              </>
            )}
          </div>
          <div className="message-time">{formatTime(new Date(message.timestamp))}</div>
        </div>
        
        <div className={`message-bubble ${isBot ? 'bot-bubble' : 'user-bubble'}`}>
          <div className="message-text">
            {message.text.split('\n').map((line, i) => (
              <React.Fragment key={i}>
                {line}
                {i < message.text.split('\n').length - 1 && <br />}
              </React.Fragment>
            ))}
          </div>
        </div>

        {/* Suggestions for bot messages */}
        {isBot && message.suggestions && message.suggestions.length > 0 && (
          <div className="message-suggestions">
            <small className="suggestions-label">Quick follow-ups:</small>
            <div className="suggestions-buttons">
              {message.suggestions.map((suggestion, index) => (
                <button
                  key={index}
                  className="suggestion-btn"
                  onClick={() => handleStarterClick(suggestion)}
                  disabled={isLoading}
                >
                  {suggestion}
                </button>
              ))}
            </div>
          </div>
        )}
      </div>
    );
  };

  return (
    <>
      {/* Chatbot Toggle Button */}
      <button
        className="chatbot-toggle-btn"
        onClick={toggleChat}
        title="AI Learning Assistant"
      >
        {isOpen ? (
          <span className="close-icon">‚úï</span>
        ) : (
          <>
            <span className="chat-icon">üí¨</span>
            <span className="notification-dot"></span>
          </>
        )}
      </button>

      {/* Chat Window */}
      {isOpen && (
        <div className="chatbot-window">
          {/* Header */}
          <div className="chat-header">
            <div className="chat-header-left">
              <div className="chat-avatar">
                <span className="avatar-icon">ü§ñ</span>
              </div>
              <div className="chat-info">
                <h3 className="chat-title">AI Learning Assistant</h3>
                <div className="chat-status">
                  <span className="status-dot online"></span>
                  <span className="status-text">Online</span>
                </div>
              </div>
            </div>
            <div className="chat-header-right">
              <button 
                className="header-btn clear-btn"
                onClick={clearConversation}
                title="Clear conversation"
              >
                <span className="btn-icon">üóëÔ∏è</span>
              </button>
              <button 
                className="header-btn close-btn"
                onClick={() => setIsOpen(false)}
                title="Close chat"
              >
                <span className="btn-icon">‚úï</span>
              </button>
            </div>
          </div>

          {/* Chat Body */}
          <div className="chat-body" ref={chatContainerRef}>
            {/* Welcome Message */}
            <div className="welcome-section">
              <div className="welcome-card">
                <div className="welcome-icon">üëã</div>
                <h4 className="welcome-title">How can I help you today?</h4>
                <p className="welcome-text">
                  I can help you find courses, explain concepts, and guide your learning journey.
                  Try asking me anything about learning!
                </p>
              </div>
            </div>

            {/* Conversation Starters */}
            {conversationStarters.length > 0 && (
              <div className="starters-section">
                <h5 className="starters-title">üí° Try asking:</h5>
                <div className="starters-grid">
                  {conversationStarters.map((starter, index) => (
                    <button
                      key={index}
                      className="starter-card"
                      onClick={() => handleStarterClick(starter)}
                      disabled={isLoading}
                    >
                      {starter}
                    </button>
                  ))}
                </div>
              </div>
            )}

            {/* Messages */}
            <div className="messages-container">
              {messages.map(renderMessage)}
              {isLoading && (
                <div className="typing-indicator">
                  <div className="typing-dots">
                    <div className="dot"></div>
                    <div className="dot"></div>
                    <div className="dot"></div>
                  </div>
                  <span className="typing-text">AI is thinking...</span>
                </div>
              )}
              <div ref={messagesEndRef} />
            </div>
          </div>

          {/* Chat Footer */}
          <div className="chat-footer">
            <div className="input-container">
              <div className="input-wrapper">
                <textarea
                  className="chat-input"
                  placeholder="Ask me anything about courses or learning..."
                  value={input}
                  onChange={(e) => setInput(e.target.value)}
                  onKeyPress={handleKeyPress}
                  disabled={isLoading}
                  rows="1"
                />
                <div className="input-actions">
                  <button
                    className="send-btn"
                    onClick={() => sendMessage()}
                    disabled={isLoading || !input.trim()}
                    title="Send message"
                  >
                    {isLoading ? (
                      <span className="spinner"></span>
                    ) : (
                      <span className="send-icon">‚û§</span>
                    )}
                  </button>
                </div>
              </div>
              
              <div className="input-hints">
                <small className="hint-text">
                  Press <kbd>Enter</kbd> to send ‚Ä¢ <kbd>Shift</kbd> + <kbd>Enter</kbd> for new line
                </small>
              </div>
            </div>
          </div>
        </div>
      )}
    </>
  );
};

export default AIChatbot;
