import React, { createContext, useState, useContext, useEffect } from 'react';
import ApiService from '../services/api';

const DataContext = createContext();

export const useData = () => useContext(DataContext);

export const DataProvider = ({ children }) => {
  const [courses, setCourses] = useState([
    // Hardcoded courses for now - from your curl response
    {"id":1,"name":"React for Beginners","description":"Learn React fundamentals and build your first application.","duration":"4 weeks","level":"Beginner","category":"Web Development","instructor":"Sarah Johnson","price":"$49.99","rating":4.8,"students":1250,"modules":12,"image":"https://images.unsplash.com/photo-1633356122544-f134324a6cee?w=400"},
    {"id":2,"name":"Intro to Data Science","description":"Master data analysis and machine learning concepts with Python.","duration":"6 weeks","level":"Intermediate","category":"Data Science","instructor":"Michael Chen","price":"$69.99","rating":4.7,"students":890,"modules":16,"image":"https://images.unsplash.com/photo-1551288049-bebda4e38f71?w=400"},
    // Add more if needed
  ]);
  
  const [userCourses, setUserCourses] = useState([]);
  const [schools, setSchools] = useState([]);
  const [loading, setLoading] = useState(false); // Set to false since we're using hardcoded data
  const [currentUser, setCurrentUser] = useState({
    id: 'user123',
    name: 'John Doe',
    email: 'john@example.com'
  });

  // In DataContext.js - Update the useEffect
useEffect(() => {
  const loadInitialData = async () => {
    try {
      setLoading(true);
      
      // Fetch user's enrollments
      const userEnrollments = await ApiService.getUserEnrollments(currentUser.id);
      if (userEnrollments.success) {
        setUserCourses(userEnrollments.data || []);
      }
      
      // Fetch all available courses (from port 5001)
      const coursesData = await ApiService.getCourses();
      setCourses(coursesData || []);
      
      // Fetch schools
      const schoolsData = await ApiService.getSchools();
      setSchools(schoolsData || []);
      
    } catch (error) {
      console.error('Error loading data:', error);
    } finally {
      setLoading(false);
    }
  };

  loadInitialData();
}, [currentUser.id]);

  // For school management
  const addSchool = async (schoolData) => {
    try {
      // Try to use the real API if it exists
      if (ApiService.addSchool) {
        const result = await ApiService.addSchool(schoolData);
        if (result.success || result._id) {
          const schoolsData = await ApiService.getSchools();
          setSchools(schoolsData || []);
          return { success: true, data: result };
        }
        return { success: false, error: result.error };
      } else {
        // Mock response for presentation
        const newSchool = {
          _id: Date.now().toString(),
          ...schoolData,
          createdAt: new Date().toISOString()
        };
        setSchools(prev => [...prev, newSchool]);
        return { success: true, data: newSchool };
      }
    } catch (error) {
      return { success: false, error: 'Failed to add school' };
    }
  };

  // For course enrollment
  const enrollInCourse = async (courseId) => {
    if (ApiService.enrollCourse) {
      const result = await ApiService.enrollCourse(currentUser.id, courseId);
      if (result.success) {
        return { success: true, message: 'Enrolled successfully!' };
      }
      return { success: false, message: result.error };
    }
    
    // Mock enrollment for presentation
    const course = courses.find(c => c.id === courseId || c._id === courseId);
    if (course) {
      setUserCourses(prev => [...prev, { 
        ...course, 
        enrolledDate: new Date().toISOString(),
        progress: 0 
      }]);
      return { success: true, message: 'Enrolled successfully!' };
    }
    return { success: false, message: 'Course not found' };
  };

  // For AI recommendations
  const getRecommendations = async (interest) => {
    if (ApiService.getAiRecommendations) {
      return await ApiService.getAiRecommendations(interest);
    }
    
    // Mock recommendations for presentation
    return {
      success: true,
      data: {
        recommendations: courses
          .filter(c => c.category.toLowerCase().includes(interest.toLowerCase()) || 
                      c.name.toLowerCase().includes(interest.toLowerCase()))
          .slice(0, 3)
      }
    };
  };

  return (
    <DataContext.Provider value={{
      courses,
      userCourses,
      schools,
      loading,
      currentUser,
      enrollInCourse,
      addSchool,
      getRecommendations,
      refreshData: () => {}, // Empty function for now
      setCurrentUser
    }}>
      {children}
    </DataContext.Provider>
  );
};
